import React, {useState} from 'react'
import { connect } from 'react-redux'

import CustomButton from '../custom-button/cutom-button.component'
import {addItem} from '../../redux/cart/cart.actions'

import './collection-item.styles.scss'
import ImagePreview from '../image-preview/image-preview.component'

const CollectionItem = ({ item, addItem }) => {
    const { name, price, type, imageUrl, images} = item
    const [showImages, setShowImages] = useState(false)
    const [image, setImage] = useState(imageUrl)

    // if(showImages) {
    //     let images = (
    //     <><img src = {imageUrl} alt = {name} onClick = {() => setImage(imageUrl)} className = 'image'/>
    //         {images.map(image => (
    //         <img src={image} alt={name} onClick = {() => setImage(image)}/>
    //     ))}</>
    //     )
    // }

    // else {
    //     let images = <span>{images.length + 1} Images</span>
    // }

    return (
    <div className="collection-item">
        <img src = {image} alt = {name} className = 'image'/>

        <div className="collection-footer">
            <div className="collection-footer-upper">
                <span className="name">{name}</span>
                <span className="price">${price}</span>
            </div>
            <span className="type">{type}</span>

            <div className="images" 
                onMouseEnter = {() => setShowImages(true)} 
                onMouseLeave = {() => {
                    setShowImages(false)
                    setImage(imageUrl)}}>

                <ImagePreview item = {item}/>            
            </div>

        </div>
        {/* <CustomButton onClick = {() => addItem(item)} inverted>{price}<i class="fas fa-cart-arrow-down"></i> </CustomButton> */}
    </div>
)}

const mapDispatchToProps = dispatch => ({
    addItem: item => dispatch(addItem(item))
})

export default connect(null, mapDispatchToProps)(CollectionItem)
