import React from 'react'
import { connect } from 'react-redux'
import {createStructuredSelector} from 'reselect'
import {withRouter} from 'react-router-dom'

import CustomButton from '../custom-button/cutom-button.component'
import CartItem from '../cart-item/cart-item.component'

import {selectCartItems, 
        selectCartItemsCount,
        selectCartHidden} 
        from '../../redux/cart/cart.selectors'


import {toggleCartHidden} from '../../redux/cart/cart.actions'

import './cart-dropdown.styles.scss'

const CartDropdown = ({ cartItems, hidden, history, dispatch, itemCount }) => {

    return (
    <>
    <div className="cart-dropdown">
        <div className="success-message">
            <i class="fas fa-check"></i> Added to Bag
        </div>
        <div className="cart-items">
            {
                cartItems.length ? (
                <CartItem key = {cartItems[cartItems.length -1].id} item ={cartItems[cartItems.length -1]} />
            ) : (
                <span className="empty-message">Your cart is empty</span>
            )
            }
        </div>
        <div className="cart-buttons">
            <CustomButton inverted
                onClick = {() => {
                history.push('/checkout')
                dispatch(toggleCartHidden())}}>
                View Bag ({itemCount})
            </CustomButton>
            <CustomButton 
                onClick = {() => {
                history.push('/checkout')
                dispatch(toggleCartHidden())}}>
                Checkout
            </CustomButton>
        </div>
    </div>
    {!hidden && <div className="screen-overlay"></div>}
</>
)}

const mapStateToProps = createStructuredSelector({
    cartItems: selectCartItems,
    itemCount: selectCartItemsCount,
    hidden: selectCartHidden

})

export default withRouter(connect(mapStateToProps)(CartDropdown))
