import React from 'react'

const FOOTER_ABOUT_DATA = [
    {
        id: 0,
        link: 'news'
    },
    {
        id: 1,
        link: 'careers'
    },
    {
        id: 2,
        link: 'investors'
    },
    {
        id: 3,
        link: 'sustainability'
    }
]

const FOOTER_CUSTOMER_DATA = [
    {
        id: 0,
        link: 'find a store'
    },
    {
        id: 1,
        link: 'sign up for email'
    },
    {
        id: 2,
        link: 'become a member'
    },
    {
        id: 3,
        link: 'site feedback'
    }
]

const FOOTER_HELP_DATA = [
    {
        id: 0,
        link: 'order status'
    },
    {
        id: 1,
        link: 'shipping and delivery'
    },
    {
        id: 2,
        link: 'returns'
    },
    {
        id: 3,
        link: 'payment options'
    },
    {
        id: 4,
        link: 'contact us'
    }
]

const FooterContainer = () => {
    return (
        <div className="footer-container">

            <ul className = 'footer-navigation-container'>
                {
                    FOOTER_CUSTOMER_DATA.map(item => (
                        <li>{item.link.toUpperCase()}</li>
                    ))
                }
            </ul>

            <ul className = 'footer-navigation-container'>
                <h2 className="title">Get Help</h2>
                {
                    FOOTER_HELP_DATA.map(item => (
                        <li>{item.link}</li>
                    ))
                }
            </ul>

            <ul className = 'footer-navigation-container'>
                <h2 className="title">About Nike</h2>
                {
                    FOOTER_ABOUT_DATA.map(item => (
                        <li>{item.link}</li>
                    ))
                }
            </ul>
        </div>
    )
}

export default FooterContainer
