import React, {Fragment} from 'react'

import './size-chart.styles.scss'

const SizeChart = ({handleChange}) => {
    let links = []
    for(let i = 5; i <= 12; i++) {
        links.push(
        <Fragment>
            <div 
                className = 'size-option'
                onChange = {handleChange}
            >
               <span> US {i}</span>
            </div>
            <div 
                className = 'size-option' 
                onChange = {handleChange}
            >
                 US {i}.5
            </div>
        </Fragment>
        )
    }
    return (
        <div className = 'size-chart'>
            <h3>Select Size</h3>
            {links}
        </div>
    )
}

export default SizeChart
