import React, {useState, useEffect} from 'react'

import {connect} from 'react-redux'

import {filterCollectionByPrice} from '../../redux/shop/shop.actions'
import {selectCollection} from '../../redux/shop/shop.selectors'

import CollectionsContainer from '../../components/collection-container/collection-container.component'
import CollectionPreview from '../../components/preview-collection/preview-collection.component'

import './collection.styles.scss'

const CollectionPage = ({ collection, match, filterCollectionByPrice }) => {
    const {items} = collection

    return (
        <CollectionsContainer  handleClick = {filterCollectionByPrice}>
            <CollectionPreview items = {items}/>
        </CollectionsContainer>
)}

const mapStateToProps = (state, ownProps) => ({
    collection: selectCollection(ownProps.match.params.collectionId)(state),
})

const mapDispatchToProps = dispatch => ({
    filterCollectionByPrice: () => dispatch(filterCollectionByPrice())
})

export default connect(mapStateToProps, mapDispatchToProps)(CollectionPage)
