import React from 'react'
import {connect} from 'react-redux'
import {createStructuredSelector} from 'reselect'

import {selectCollectionTags, selectFilterMenu} from '../../redux/shop/shop.selectors'

import {FilterMenuContainer, 
        TagsListContainer,
        TagItem} from './filter-menu.styles'

const FilterMenu = ({tags, hidden, handleClick}) => {
    const tagsFlatMap = [...new Set(tags[0])]

    return (
        <FilterMenuContainer isVisible = {hidden}>
           <TagsListContainer>
               {
                tagsFlatMap.map(item => (
                       <TagItem onClick = {handleClick}>{item}</TagItem>
                   ))
               }
           </TagsListContainer>
        </FilterMenuContainer>
    )
}

const mapStateToProps = createStructuredSelector({
    tags: selectCollectionTags,
    hidden: selectFilterMenu
})

export default connect(mapStateToProps)(FilterMenu)
