import React, {useState, useEffect} from 'react'
import {connect} from 'react-redux'
import {createStructuredSelector} from 'reselect'
import { selectCollectionByFilter } from '../../redux/shop/shop.selectors'
import CollectionPreview from '../../components/preview-collection/preview-collection.component'

import CollectionsContainer from '../../components/collection-container/collection-container.component'
import CollectionsOverview from '../../components/collections-overview/collections-overview.component'


const FilteredResultsPage = ({ collection, match }) => {
    // console.log(match.params.filter)
    const items = collection.map(el => el.items)
    const tags = items.map(el => el.map(i => i.tags).flat(1));
    const tagsFlatMap = [...new Set(tags[0])]
    // const filteredData = items.filter(item => item.tags.includes(match.params.filter));
    // console.log(filteredData)

    return (
        <CollectionsContainer tags = {tags} title="See What's New">
            <CollectionPreview items = {items}/>
        </CollectionsContainer>
)}

const mapStateToProps = (state, ownProps) => ({
    collection: selectCollectionByFilter(ownProps.match.params.filter)(state),
})

export default connect(mapStateToProps)(FilteredResultsPage)
