import styled, {css} from 'styled-components'

export const FilterIconContainer = styled.span`
    display: inline-block;
    width: 20px;
    border-top: 3px solid #333;
    border-bottom: 3px solid #333;
    height: 10px;
    margin-left: 9px;
    position: relative;

    &:before, 
    &:after {
        position: absolute;
        content: '';
        height: 7px;
        width: 2px;
        background: #333;
    }

    &:before {
        top: -5px;
        left: ${props => props.active ? '5px' : '15px'};
    }

    &:after {
        bottom: -5px;
        right: 5px;
    }
}
`
