import React, {useState, useEffect} from 'react'

import {connect} from 'react-redux'
import {selectCollection} from '../../redux/shop/shop.selectors'

import CollectionsContainer from '../../components/collection-container/collection-container.component'
import CollectionPreview from '../../components/preview-collection/preview-collection.component'


const FilteredResultsPage = ({ collection }) => {
    const {items, title} = collection
    const [filteredCollection, setFilteredCollection] = useState([])

    const tags = items.map(el => el.tags).flat(1);
    const tagsFlatMap = [...new Set(tags)]

    const filteredData = items.filter(el => el.name.toLowerCase().includes('zoom'));

    const handleClick = event => {
        const sortedData = items.sort((a, b) => parseFloat(a.price) - parseFloat(b.price))
        setFilteredCollection(sortedData)
    }
    return (
        <CollectionsContainer tags= {tagsFlatMap} title = {title} handleClick = {handleClick}>
            <CollectionPreview items = {items}/>
        </CollectionsContainer>
)}

const mapStateToProps = (state, ownProps) => ({
    collection: selectCollection('women')(state),
})

export default connect(mapStateToProps)(FilteredResultsPage)
