import React, {Fragment} from 'react'

const SizeChart = () => {
    let links = []
    for(let i = 5; i <= 12; i++) {
        links.push(
        <Fragment>
            <div>
                <input type="radio" name="size" id={i}/>
                <label for = {i}>US {i}</label>
            </div>
            <div>
                <input type="radio" name="size" id={`${i}.5`}/>
                <label for = {`${i}.5`}>{i}.5</label>
            </div>
        </Fragment>
        )
    }
    return (
        <div className = 'size-chart'>
            {links}
        </div>
    )
}

export default SizeChart
