import { createSelector } from 'reselect'

const selectCart = state => state.cart;

export const selectCartItems = createSelector(
    [selectCart],
    cart => cart.cartItems
)

export const selectCartHidden = createSelector(
    [selectCart],
    cart => cart.hidden  
)

export const selectCartItemsCount = createSelector(
    [selectCartItems],
    cartItems => 
    cartItems.reduce(
        (accumlatedQuantity, cartItem) => 
        accumlatedQuantity + cartItem.quantity, 0
    )
)

export const selectCartSubtotal = createSelector(
    [selectCartItems],
    cartItems => 
    cartItems.reduce(
        (accumlatedQuantity, cartItem) => 
        accumlatedQuantity + cartItem.quantity * cartItem.price, 0
    )
)

export const selectCartTaxes = createSelector(
    [selectCartSubtotal],
    cartItems => (cartItems * 0.13).toFixed(2)
)

export const selectCartTotal = createSelector(
    [selectCartSubtotal, selectCartTaxes],
    (cartItems, taxes) => parseInt(cartItems + taxes)
)
