import styled from 'styled-components'

export const FilterMenuContainer = styled.div`
    position: fixed;
    top: 200px;
    min-width: 300px;
    background: white;
    overflow: scroll;
    height: 1500px;
    padding-right: 30px;
    grid-row: 1/-1;
    grid-column: 1/2;
    transition: all .5s ease;
    opacity: ${props => props.isVisible ? 0 : 1};
    transform: ${props => props.isVisible ? 'translateX(-500px)' : 'translateX(0px)'};
`

export const TagsListContainer = styled.ul`
    list-style-type: none;
    padding-bottom: 20px;
    border-bottom: 1px solid rgba(18, 18, 18, .2);
`

export const TagItem = styled.li`
    padding: 8px 0;
    font-weight: 500;
    font-size: 20px;
    cursor: pointer;

    &:hover {
        color: #757575;
    }
`

export const FilterOptionsContainer = styled.div`
    border-bottom: 1px solid rgba(18, 18, 18, .2);
`

export const FilterOptionsHeader = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 13px 0;
`

export const FilterOptions = styled.div`
    padding: 15px 0;
`

export const FormGroup = styled.div`
    padding: 5px 0;

    input[type="checkbox"] {
        margin-right: 5px;
    }

    label {
        font-weight: 500;
        font-size: 18px;
    }
`
