import React, {useEffect} from 'react'
import {Route} from 'react-router-dom'
import {connect} from 'react-redux'

import {fetchCollectionsStart} from '../../redux/shop/shop.actions'

import CollectionsOverviewContainer from '../../components/collections-overview/collections-overview.container'
import CollectionPageContainer from '../collection/collection.container'
import CollectionDetailsContainer from '../collection-details/collection-details.container'

import './shop.styles.scss'

const ShopPage = ({match, isLoaded, fetchCollectionsStart}) => {

    useEffect(() => {
        fetchCollectionsStart()
    }, [])

    return(
        <div className = 'shop-page'>
            <Route 
                exact path ={`${match.path}`} 
                component = {CollectionsOverviewContainer}
            />
            <Route 
                exact path = {`${match.path}/:collectionId`} 
                component = {CollectionPageContainer}
            />
            <Route 
                exact path = {`${match.path}/details/:itemId`} 
                component = {CollectionDetailsContainer}
            />
        </div>
    )
}

const mapDispatchToProps = dispatch => ({
    fetchCollectionsStart: () => dispatch(fetchCollectionsStartAsync())
})

export default connect(null, mapDispatchToProps)(ShopPage)
