import React, {useState} from 'react'

import {toggleFillterMenu} from '../../redux/shop/shop.actions'

import {FilterHeaderContainer,
        Title,
        FilterOptionsContainer,
        FilterOption,
        FilterIcon, 
        SortDropdownContainer,
        ArrowIcon
    } from './filter-header.styles'

const FilterHeader = ({title, isOpen, handleClick, setIsOpen, toggleFillterMenu}) => {
    const [toggleDropdown, setToggleDropdown] = useState(false);
    const [isPassedTop, setIsPassedTop] = useState(false)

window.addEventListener('scroll', event => {
    if(window.pageYOffset > 150) {
        setIsPassedTop(true)
    } else {
        setIsPassedTop(false)
    }
})

    return (
         <FilterHeaderContainer>
            <Title isPassedTop = {isPassedTop}>{title}</Title>
            <FilterOptionsContainer>
                <FilterOption onClick = {() => toggleFillterMenu()}>
                    {isOpen ? 'Show' : 'Hide'} Filters
                    <FilterIcon isOpen = {isOpen}></FilterIcon>
                </FilterOption>
                <FilterOption 
                    onClick = {() => setToggleDropdown(!toggleDropdown)}
                    toggleDropdown = {toggleDropdown}>
                    Sort By 
                    <ArrowIcon toggleDropdown = {toggleDropdown}>
                        <span class = 'arrow-left'></span>
                        <span class = 'arrow-left'></span>
                    </ArrowIcon>  
                </FilterOption>
            </FilterOptionsContainer>
            <SortDropdownContainer toggleDropdown = {toggleDropdown}>
                <span data-type='high' onClick = {handleClick}>Price: High-Low</span>
                <span data-type='low' onClick = {handleClick}>Price: Low-High</span>
            </SortDropdownContainer>
        </FilterHeaderContainer>
    )
}

const mapDispatchToProps = dispatch => ({
    toggleFillterMenu: () => dispatch(toggleFillterMenu())
})

export default connect(null,mapDispatchToProps)(FilterHeader)
