import React, {Fragment} from 'react'

import './size-chart.styles.scss'

const SizeChart = ({isChecked, handleChange}) => {
    let links = []
    for(let i = 5; i <= 12; i++) {
        links.push(
        <Fragment>
            <div className = 'size-option'>
                <input 
                    type="radio" 
                    name="size" 
                    id={i} 
                    checked = {isChecked} 
                    onChange = {handleChange}
                />
                <label for = {i}>US {i}</label>
            </div>
            <div 
                className = 'size-option' 
                checked = {isChecked} 
                onChange = {handleChange}>
                {i}.5
            </div>
        </Fragment>
        )
    }
    return (
        <div className = 'size-chart'>
            <h3>Select Size</h3>
            {links}
        </div>
    )
}

export default SizeChart
