import React from 'react'
import {connect} from 'react-redux'
import {selectItemDetails, selectCollectionForPreview} from '../../redux/shop/shop.selectors'
import CollectionDetails from '../../components/collection-details/collection-details.component'
import ScrollingContainer from '../../components/scrolling-wrapper/scrolling-wrapper.component'

const CollectionDetailsPage = ({collections, similarCollections}) => {

    const getSimilarCollection = (collection, value) => {
        const similar = similarCollections.map(collection => collection.items.filter(item => item.tags.includes(value)))
        const padItems = similarCollections.map(collection => collection.items.filter(item => item.tags.includes('lifestyle')))
        if(similar.length >= 12) {
            return similar
        } else {
            return [...similar, ...padItems]
        }
    }

    console.log(getSimilarCollection(similarCollections, 'basketball'))
    const similar = similarCollections.map(collection => collection.items.filter(item => item.tags.includes('basketball' || 'lifestyle')))

    const getCollection = similar.flat(1).filter((item, idx) => idx < 12)
    return (
        <div className = 'collection-details-page'>
           {collections.map(collection => (
               <CollectionDetails items = {collection}/>
            ))
            }
            <ScrollingContainer collections = {getCollection} />
        </div>
    )
}

const mapStateToProps = (state, ownProps) => ({
    collections: selectItemDetails(ownProps.match.params.itemId)(state),
    similarCollections: selectCollectionForPreview(state)
})

export default connect(mapStateToProps)(CollectionDetailsPage)
