import React from 'react'
import {connect} from 'react-redux'
import {createStructuredSelector} from 'reselect'
import { selectCollectionForPreview } from '../../redux/shop/shop.selectors'

import CollectionsContainer from '../../components/collection-container/collection-container.component'
import CollectionsOverview from '../../components/collections-overview/collections-overview.component'

const ShopPageContainer = ({ collections }) => {
    const {title, items} = collections

    console.log(collections.map(el => el.items))

    // const tags = items.map(el => el.tags).flat(1);
    // const tagsFlatMap = [...new Set(tags)]

    const tags = ['running', 'walking']

    return (
        <CollectionsContainer items = {items} tags={tags} title="See What's New">
            <CollectionsOverview collections = {collections}/>
        </CollectionsContainer>
)}

const mapStateToProps = createStructuredSelector({
    collections: selectCollectionForPreview
})

export default connect(mapStateToProps)(ShopPageContainer)
