import React from 'react'
import {animated, useSpring, useTransition, config} from 'react-spring'
import { connect } from 'react-redux'
import {createStructuredSelector} from 'reselect'
import {withRouter} from 'react-router-dom'

import CustomButton from '../custom-button/cutom-button.component'
import CartItem from '../cart-item/cart-item.component'

import {selectCartItems, selectCartItemsCount, selectSuccessMessage} from '../../redux/cart/cart.selectors'

import {toggleCartHidden} from '../../redux/cart/cart.actions'

import './cart-dropdown.styles.scss'

const CartDropdown = ({ cartItems, history, isOpen, dispatch, successMessage, itemCount }) => {

    const transitions = useTransition(isOpen, null, {
        config: config.default,
        from: { transform:'translateX(-500px)', opacity: 0},
        enter: { transform:'translateX(0px)', opacity: 1},
        leave: { transform:'translateX(-500px)', opacity: 0}
    })

    return transitions.map(({ item, props}) => item && (
    <animated.div style = {props} className="cart-dropdown">
        <div className="success-message">
             {successMessage}
        </div>
        <div className="cart-items">
            {
                cartItems.length ? (
                <CartItem 
                    key = {cartItems[cartItems.length -1].id} 
                    item ={cartItems[cartItems.length -1]} />
            ) : (
                <span className="empty-message">Your cart is empty</span>
            )
            }
        </div>
        <div className="cart-buttons">
            <CustomButton inverted
                onClick = {() => {
                history.push('/checkout')
                dispatch(toggleCartHidden())}}>
                View Bag ({itemCount})
            </CustomButton>
            <CustomButton 
                onClick = {() => {
                history.push('/checkout')
                dispatch(toggleCartHidden())}}>
                Checkout
            </CustomButton>
        </div>
    </animated.div>
))}

const mapStateToProps = createStructuredSelector({
    cartItems: selectCartItems,
    itemCount: selectCartItemsCount,
    successMessage: selectSuccessMessage
})

export default withRouter(connect(mapStateToProps)(CartDropdown))
