import React, {useState} from 'react'
import {connect} from 'react-redux'
import {createStructuredSelector} from 'reselect'

import {selectCollectionTags, selectFilterMenu} from '../../redux/shop/shop.selectors'

import Checkbox from '../checkbox/checkbox.component'
import {ArrowIcon} from '../icons/icons.component'

import {FilterMenuContainer, 
        TagsListContainer,
        TagItem,
        FilterOptionsHeader,
        FilterOptionsContainer,
        FilterOptions,
        CollapsibleContainer
    } from './filter-menu.styles'

const FilterContainer = ({title, children}) => {
    const [isOpen, setIsOpen] = useState(true)

    return (
           <FilterOptionsContainer>
               <FilterOptionsHeader onClick = {() => setIsOpen(!isOpen)}>
                   <h4>{title}</h4>
                   <ArrowIcon isDropdownHidden = {!isOpen} />
               </FilterOptionsHeader>

               <FilterOptions isOpen = {isOpen}>
                <CollapsibleContainer isOpen = {isOpen}>
                  {children}
                </CollapsibleContainer>
                </FilterOptions>
           </FilterOptionsContainer>
    )
}

const GENDER_FILTER_DATA = [
    {
        id: 0,
        label: 'men'
    },
    {
        id: 1,
        label: 'women'
    },
    {
        id: 2,
        label: 'kids'
    }
]

const PRICE_FILTER_DATA = [
    {
        id: 0,
        label: 'first'
    },
    {
        id: 1,
        label: 'second'
    },
    {
        id: 2,
        label: 'third'
    },
    {
        id: 3,
        label: 'fourth'
    }
]

const FilterMenu = ({tags, hidden, fetchFilteredCollection}) => {

    const TAGS_FILTER_DATA = [...new Set(tags[0])]

    return (
        <FilterMenuContainer isVisible = {hidden}>
           <TagsListContainer>
            {
                TAGS_FILTER_DATA.map(item => (
                   <TagItem onClick = {fetchFilteredCollection}>{item}</TagItem>
                ))
            }
           </TagsListContainer>
            <FilterContainer title = 'Gender'>
            {
                GENDER_FILTER_DATA.map(({...props })=> (
                    <Checkbox {...props} />
                ))
            }
            </FilterContainer>

            <FilterContainer title = 'Shop By Price'>
            {
                PRICE_FILTER_DATA.map(({...props })=> (
                    <Checkbox isPriceLabel {...props} />
                ))
            }
            </FilterContainer>
        </FilterMenuContainer>
    )
}

const mapStateToProps = createStructuredSelector({
    tags: selectCollectionTags,
    hidden: selectFilterMenu,
})

export default connect(mapStateToProps)(FilterMenu)
