import styled, {css} from 'styled-components'

export const FilterIcon = styled.span`
    display: inline-block;
    width: 20px;
    border-top: 3px solid #333;
    border-bottom: 3px solid #333;
    height: 10px;
    margin-left: 9px;
    position: relative;

    &:before, 
    &:after {
        position: absolute;
        content: '';
        height: 7px;
        width: 2px;
        background: #333;
        transition: all .5s ease;
    }

    &:before {
        top: -5px;
        left: ${props => props.isOpen ? '5px' : '13px'};
    }

    &:after {
        bottom: -5px;
        right: ${props => props.isOpen ? '5px' : '13px'};
    }
}
`

export const FilterHeaderContainer = styled.div`
    position: sticky;
    top: 110px;
    background: white;
    width: 100%;
    z-index: 1000;
    padding: 30px 10px;
    display: flex;
    justify-content: space-between;
`

export const Title = styled.h2`
    font-size: 25px;
    font-weight: 500;
`

export const FilterOptionsContainer = styled.div`
    display: flex;
    align-items: center;
    position: relative;
    background: white;
    z-index: 10000;
`

export const FilterOption = styled.div`
    cursor: pointer;
    display: flex;
    align-items: center;
    margin-left: 20px;

    .fas {
        padding-left: 8px;
        transition: all .5s ease;
        transform: ${props => props.toggleDropdown ? 'rotate(180deg)' : 'rotate(0deg)'};
    }
`

export const ArrowIcon = styled.div`
    width: 1rem;
    height: 1.25rem;
    background: red;
    position: relative;

    span {
        width: .75rem;
        height: .1rem;
        background: black;
        display: inline-block;
        position: absolute;
        top: .5rem;
        transition: all .3s ease;

        &:first-of-type {
            left: 0;
            transform: ${props => props.toggleDropdown ? 'rotate(-45deg)' : 'rotate(45deg)'};
        }

        &:last-of-type {
            right: 0;
            transform: ${props => props.toggleDropdown ? 'rotate(45deg)' : 'rotate(-45deg)'};
        }
    }
`

export const SortDropdownContainer = styled.div`
    position: absolute;
    top: 50px;
    right: -10px;
    background: white;
    display: flex;
    flex-direction: column;
    padding: 30px 10px;
    border-radius: 0 0 20px 20px;
    transition: all .5s ease;
    transform: ${props => props.toggleDropdown ? 'translateY(0px)' : 'translateY(-180px)'};
    opacity: ${props => props.toggleDropdown ? 1:0};
    text-align: right;
    z-index: -1;

    span {
        padding: 5px 0;
    }
`
