import React, {Fragment, useState} from 'react'
import {connect} from 'react-redux'
import {createStructuredSelector} from 'reselect'

import Checkbox from '../checkbox/checkbox.component'

import {selectCartItems} from '../../redux/cart/cart.selectors.js'

import './size-chart.styles.scss'

const SizeChart = ({cartItems, handleSubmit, isValid, children, ...props}) => {
    const [isChecked, setIsChecked] = useState(false)

    const handleChange = event => {
        setIsChecked(true)
        cartItems.push({size: event.target.value})
    }

    let links = []
    for(let i = 5; i < 12; i++) {
        links.push(
        <Fragment>
           <Checkbox type = {i} {...props} handleChange = {handleChange}/>
           <Checkbox type = {`${i}.5`} {...props} handleChange = {handleChange}/>
        </Fragment>
        )
    }

    return (
        <form className = {`size-chart ${isValid && 'error-message'}`} onSubmit = {handleSubmit}>
            <h3>Select Size</h3>
            {links}
            {children}
        </form>
    )
}

const mapStateToProps = createStructuredSelector({
    cartItems: selectCartItems
})

export default connect(mapStateToProps)(SizeChart)
