import React from 'react'
import {connect} from 'react-redux'
import {getItemDetails} from '../../redux/shop/shop.selectors'

const CollectionDetailsPage = ({item}) => {
    const colour = item.map(el => el.map(x => x.colour));
    return (
        <div>
           {colour}
        </div>
    )
}

const mapStateToProps = (state, ownProps) => ({
    item: getItemDetails(ownProps.match.params.itemId)(state)
})

export default connect(mapStateToProps)(CollectionDetailsPage)
