import React, {useState} from 'react'
import {connect} from 'react-redux'
import {createStructuredSelector} from 'reselect'

import {selectCollectionTags, selectFilterMenu} from '../../redux/shop/shop.selectors'

import {ArrowIcon} from '../icons/icons.component'

import {FilterMenuContainer, 
        TagsListContainer,
        TagItem,
        FilterOptionsHeader,
        FilterOptionsContainer,
        FilterOptions,
        FormGroup,
        CustomCheckbox
    } from './filter-menu.styles'

const FilterOption = ({item, isPriceLabel}) => {

    const {tier, min, max} = item;
    let label = '';
    if(isPriceLabel) {
        label = max ? `CAD ${min} - CAD ${max} `
        : `CAD ${min}+`
    }
    else {
        label = 'Hello'
    }

    return (
        <FormGroup>
            <CustomCheckbox />
            <input type="checkbox" id={tier} name={tier} value={tier} />
            <label for={tier}>
                {
                    label
                }

            </label>
        </FormGroup>

)}

const PRICE_FILTER_DATA = [
    {
        tier: 'first',
        min: 0,
        max: 74
    },
    {
        tier: 'second',
        min: 74,
        max: 150
    },
    {
        tier: 'third',
        min: 150,
        max: 220
    },
    {
        tier: 'fourth',
        min: 220
    }
]

const FilterMenu = ({tags, hidden, fetchFilteredCollection}) => {

    const tagsFlatMap = [...new Set(tags[0])]
    const [isOpen, setIsOpen] = useState(false)

    return (
        <FilterMenuContainer isVisible = {hidden}>
           <TagsListContainer>
               {
                tagsFlatMap.map(item => (
                       <TagItem onClick = {fetchFilteredCollection}>{item}</TagItem>
                   ))
               }
           </TagsListContainer>

           <FilterOptionsContainer>
               <FilterOptionsHeader>
                   <h4>Gender</h4>
                   <ArrowIcon />
               </FilterOptionsHeader>

               <FilterOptions>
                    <FormGroup>
                        <CustomCheckbox />
                        <input type="checkbox" id="men" name="men" value="men" />
                        <label for="men">Men</label>
                    </FormGroup>

                    <FormGroup>
                        <CustomCheckbox />
                        <input type="checkbox" id="women" name="women" value="women" />
                        <label for="women">Women</label>
                    </FormGroup>
                </FilterOptions>
           </FilterOptionsContainer>

           <FilterOptionsContainer>
               <FilterOptionsHeader onClick = {() => setIsOpen(!isOpen)}>
                   <h4>Shop By Price</h4>
                   <ArrowIcon />
               </FilterOptionsHeader>

               <FilterOptions isOpen = {isOpen}>
                    {
                        PRICE_FILTER_DATA.map(item => (
                            <FilterOption isPriceLabel item = {item} />
                        ))
                    }

                </FilterOptions>
           </FilterOptionsContainer>
        </FilterMenuContainer>
    )
}

const mapStateToProps = createStructuredSelector({
    tags: selectCollectionTags,
    hidden: selectFilterMenu,
})

export default connect(mapStateToProps)(FilterMenu)
