import styled, {css} from 'styled-components'

export const FilterIcon = styled.span`
    display: inline-block;
    width: 20px;
    border-top: 3px solid #333;
    border-bottom: 3px solid #333;
    height: 10px;
    margin-left: 9px;
    position: relative;

    &:before, 
    &:after {
        position: absolute;
        content: '';
        height: 7px;
        width: 2px;
        background: #333;
        transition: all .5s ease;
    }

    &:before {
        top: -5px;
        left: ${props => props.isOpen ? '5px' : '13px'};
    }

    &:after {
        bottom: -5px;
        right: ${props => props.isOpen ? '5px' : '13px'};
    }
}
`

export const FilterHeaderContainer = styled.div`
    position: sticky;
    top: 110px;
    background: white;
    width: 100%;
    z-index: 1000;
    padding: 30px 10px;
    display: flex;
    justify-content: space-between;
`

export const Title = styled.h2`
    font-size: 25px;
    font-weight: 500;
`

export const FilterOptionsContainer = styled.div`
    display: flex;
    align-items: center;
`

export const FilterOption = styled.div`
    cursor: pointer;
    display: flex;
    align-items: center;
    margin-left: 20px;
    position: relative;

    .fas {
        padding-left: 5px;
    }
`

export const SortDropdownContainer = styled.div`
    position: absolute;
    bottom: -100px;
    width: 100%;
    background: white;
    display: flex;
    flex-direction: column;
`
