import React, {Fragment} from 'react'

import './size-chart.styles.scss'
import Checkbox from '../checkbox/checkbox.component'

const SizeChart = ({isChecked, handleChange}) => {
    let links = []
    for(let i = 5; i < 12; i++) {
        links.push(
        <Fragment>
           <Checkbox type = {i} />
           <Checkbox type = {`${i}.5`}/>
        </Fragment>
        )
    }
    return (
        <div className = 'size-chart'>
            <h3>Select Size</h3>
            {links}
        </div>
    )
}

export default SizeChart
