import React, { lazy, Suspense, useEffect, useState } from 'react';
import { Route, Switch, Redirect } from 'react-router-dom'
import {connect} from 'react-redux'
import {createStructuredSelector} from 'reselect'

import Navbar from './components/navbar/navbar.component'

import { 
      auth, 
      createUserProfileDocument, 
      addCollectionAndDocuments 
    } from './firebase/firebase.utils'

import {checkUserSession } from './redux/user/user.actions';

import { selectCartHidden } from './redux/cart/cart.selectors'
import { selectCurrentUser } from './redux/user/user.selector';

// import {selectCollectionForPreview} from './redux/shop/shop.selectors'

import './App.scss';

const Homepage = lazy(() => import('./pages/homepage/homepage.component'))
const ShopPage = lazy(() => import('./pages/shop/shop.component'))
const SignInAndSignUpPage = lazy(() => import('./pages/sign-in-and-sign-up/sign-in-and-sign-up.component'))
const CheckoutPage = lazy(() => import('./pages/checkout/checkout.component'))
const CollectionDetailsPage = lazy(() => import('./pages/collection-details/collection-details.component'))

const App = (
      {// collectionsArray, 
      checkUserSession,
      hidden, 
      toggleCartHidden,
      currentUser}) =>  {

  let unsubscribeFromAuth = null;

  useEffect(() => {
    checkUserSession()
  }, [checkUserSession])


  const [name, setName] = useState('Cassie')

  const handleChange = () => {
    if(document.hidden) {
      document.title = 'COME BACK!!'
    } else {
      document.title = 'Nike'
    }
  }

  useEffect(() => {
    window.addEventListener('visibilitychange', handleChange)
    return () => window.removeEventListener('visibilitychange', handleChange)
  }, [])



    return (
      <div>
          <Navbar/>
          <Switch>
            <Suspense fallback = {<div>LOADING...</div>}>
              <Route exact path='/' component={Homepage} />
              <Route path = '/shop' component = {ShopPage} />
              <Route exact path = '/checkout' component = {CheckoutPage} />
              <Route exact path = '/login' render = {() => currentUser ? 
                  (<Redirect to = '/' />) : 
                  (<SignInAndSignUpPage />)} />
            </Suspense>
          </Switch>
      </div>
    );
  }

const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
  // collectionsArray: selectCollectionForPreview,
  hidden: selectCartHidden
})

const mapDispatchToProps = dispatch => ({
  checkUserSession: () => dispatch(checkUserSession()),
});

// connect takes in mapStateToProps and mapDispatchToProps as parameters
// pass null when you dont need to mapStateToProps
export default connect(mapStateToProps, mapDispatchToProps)(App);
