import React, {useState} from 'react'
import {connect} from 'react-redux'
import { createStructuredSelector } from 'reselect'

import {addItem} from '../../redux/cart/cart.actions'

import Video from '../video/video.component'
import CustomButton from '../custom-button/cutom-button.component'

import './collection-details.styles.scss'
import SizeChart from '../size-chart/size-chart.component'
import { selectCartHidden } from '../../redux/cart/cart.selectors'

const CollectionDetails = ({items, addItem, hidden}) => {

    const [isChecked, setIsChecked] = useState(false);

    const handleChange = (e) => console.log('clicking')

    return (
        <div className = 'collection-details'>
            {items.map(item => (
                <>
                <div className="details-image-grid">
                    <img src={item.imageUrl} alt=""/>
                    <Video url = {item.videoUrl}/>
                    {item.images.map(image => (
                        <img src={image} alt=""/>
                     ))}
                </div>

                <div className = 'details'>
                    <div className="details-upper">
                        <span>{item.type}</span>
                        <span>${item.price}</span>
                    </div>
                    <h2>{item.name}</h2>
                    <SizeChart isChecked = {isChecked} handleChange = {handleChange}/>
                    <CustomButton onClick = {() => addItem(item)}>Add to Bag</CustomButton>
                    <CustomButton inverted>Favourite <i class="far fa-heart"></i></CustomButton>
                    <p>{item.description}</p>
                    <ul><li>Colour Shown: {item.colour}</li></ul>
                </div>
                </>
            ))}
        </div>
    )
}

const mapDispatchToProps = dispatch => ({
    addItem: item => dispatch(addItem(item))
})

const mapStateToProps = createStructuredSelector({
    hidden: selectCartHidden
})

export default connect(null, mapDispatchToProps)(CollectionDetails)
