import SHOP_DATA from './shop.data'

import ShopActionTypes from './shop.types'

import fetchCollectionByFilter from './shop.utilies'

const INITIAL_STATE = {
    collections: null,
    isFetching: false,
    errorMessage: undefined
}

const shopReducer = (state = INITIAL_STATE, action) => {
    switch(action.type) {
        case ShopActionTypes.FETCH_COLLECTIONS_START:
            return {
                ...state,
                isFetching: true
            }
        case ShopActionTypes.FETCH_COLLECTIONS_SUCCESS:
            return {
                ...state,
                isFetching: false,
                collections: action.payload
            }
        case ShopActionTypes.FETCH_COLLECTIONS_FAILURE:
            return {
                ...state,
                isFetching: false,
                errorMessage: action.payload
            }
        case ShopActionTypes.FETCH_COLLECTIONS_BY_FILTER:
            return {
                ...state,
                isFetching: false,
                collections: fetchCollectionByFilter(state.collections, action.payload)
            }
        default: 
            return state
    }
}

export default shopReducer
