import React, {useState} from 'react'
import {animated, useSpring, useTransition, config} from 'react-spring'

import './filter-menu.styles.scss'

const FilterMenu = ({isVisible, items}) => {
    const [value, setValue] = useState('');

    const props = useSpring({
        config: config.default,
        transform: isVisible ? 'translateX(-300px)' : 'translateX(0px)',
        display: isVisible ? 'none' : 'block'
    })

    const handleClick = event => {
        console.log(event.target.textContent)
    }

    // useEffect(() => {
    //     let list= [];
    //     items.map(item => {
    //         if(item.tags.includes('walking')) {
    //             list.push(item)
    //         }
    //         setFilteredCollection(list)
    //     })
    // },[])

    return (
        <animated.div style= {props} className = 'filter-menu'>
           <ul className="sort_by_tags">
               {
                   items.map(item => (
                       <li onClick = {handleClick}>{item}</li>
                   ))
               }
           </ul>
        </animated.div>
    )
}

export default FilterMenu
