import React, {useState, useEffect} from 'react'

import {connect} from 'react-redux'
import {selectCollection} from '../../redux/shop/shop.selectors'

import CollectionsContainer from '../../components/collection-container/collection-container.component'
import CollectionPreview from '../../components/preview-collection/preview-collection.component'

import './collection.styles.scss'

const CollectionPage = ({ collection }) => {
    const {title, items} = collection
    const [filteredCollection, setFilteredCollection] = useState([])

    const handleClick = event => {
        const sortedData = items.sort((a, b) => parseFloat(a.price) - parseFloat(b.price))
        setFilteredCollection(sortedData)
    }

    return (
        <CollectionsContainer title = {title} handleClick = {handleClick}>
            <CollectionPreview items = {items}/>
        </CollectionsContainer>
)}

const mapStateToProps = (state, ownProps) => ({
    collection: selectCollection(ownProps.match.params.collectionId)(state),
})

export default connect(mapStateToProps)(CollectionPage)
