import React, {useState, useEffect} from 'react'

import {connect} from 'react-redux'
import {selectCollection} from '../../redux/shop/shop.selectors'

import CollectionsContainer from '../../components/collection-container/collection-container.component'
import CollectionPreview from '../../components/preview-collection/preview-collection.component'


const FilteredResultsPage = ({ collection }) => {
    const items = collections.map(el => el.items)
    const tags = items.map(el => el.map(i => i.tags).flat(1));
    const tagsFlatMap = [...new Set(tags[0])]

    return (
        <CollectionsContainer tags={tagsFlatMap} title="See What's New">
            <CollectionsOverview collections = {collections}/>
        </CollectionsContainer>
)}

const mapStateToProps = createStructuredSelector({
    collections: selectCollectionForPreview,
})

export default connect(mapStateToProps)(FilteredResultsPage)
