import styled, {css} from 'styled-components'

export const FilterIcon = styled.span`
    display: inline-block;
    width: 20px;
    border-top: 3px solid #333;
    border-bottom: 3px solid #333;
    height: 10px;
    margin-left: 9px;
    position: relative;

    &:before, 
    &:after {
        position: absolute;
        content: '';
        height: 7px;
        width: 2px;
        background: #333;
        transition: all .3s ease;
    }

    &:before {
        top: -5px;
        left: ${props => props.isFilterMenuHidden ? '5px' : '13px'};
    }

    &:after {
        bottom: -5px;
        right: ${props => props.isFilterMenuHidden ? '5px' : '13px'};
    }
}
`

export const FilterHeaderContainer = styled.div`
    position: sticky;
    top: 110px;
    background: white;
    width: 100%;
    z-index: 1000;
    display: flex;
    justify-content: space-between;
`

export const Title = styled.h2`
    transition: all .3s ease;
    font-size: ${props => props.isPassedTop ? '22px' : '28px'};
    font-weight:500;
    padding: ${props => props.isPassedTop ? '22px 0' : '25px 0'};
    text-transform: capitalize;
`

export const FilterOptionsContainer = styled.div`
    display: flex;
    align-items: center;
    position: relative;
    background: white;
    z-index: 100;
`

export const FilterOption = styled.div`
    cursor: pointer;
    display: flex;
    align-items: center;
    margin-left: 20px;
    height: 100%;

    .fas {
        padding-left: 8px;
        transition: all .5s ease;
        transform: ${props => props.isFilterMenuHidden ? 'rotate(180deg)' : 'rotate(0deg)'};
    }
`


export const SortDropdownContainer = styled.div`
    position: absolute;
    top: 55px;
    right: -10px;
    background: white;
    display: flex;
    flex-direction: column;
    padding: 30px 10px;
    border-radius: 0 0 20px 20px;
    transition: all .3s ease;
    transform: ${props => props.isDropdownHidden ? 'translateY(-100px)' : 'translateY(0px)'};
    opacity: ${props => props.isDropdownHidden ? 0 : 1};
    text-align: right;
    z-index: -1;

    span {
        padding: 5px 0;
        cursor: pointer;

        &:hover {
            color: #757575;
        }
    }
`
