import React from 'react'
import {animated, useSpring, useTransition, config} from 'react-spring'

import './filter-menu.styles.scss'

const FilterMenu = ({isVisible}) => {

    const transitions = useTransition(isVisible, null, {
        config: config.default,
        from: {transform: 'translateX(-100px)', opacity: 0},
        enter: {transform: 'translateX(0px)', opacity: 1},
        leave: {transform: 'translateX(-100px)', opacity: 0}
    })

    const props = useSpring({
        config: config.wobbly,
        transform: isVisible ? 'translateX(50px)' : 'translateX(0px)'
    })

    return (
        <animated.div style= {props} className = 'filter-menu'>
            <h1>This is the filter menu</h1>
        </animated.div>
    )
}

export default FilterMenu
