import React, {useState} from 'react'
import {createStructuredSelector} from 'reselect'

import {connect} from 'react-redux'

import {toggleFillterMenu, toggleSortByDropdown} from '../../redux/shop/shop.actions'

import {FilterHeaderContainer,
        Title,
        FilterOptionsContainer,
        FilterOption,
        FilterIcon, 
        SortDropdownContainer,
        ArrowIcon
    } from './filter-header.styles'

import { selectFilterMenu, selectSortByDropdown } from '../../redux/shop/shop.selectors'

const FilterHeader = ({title, handleClick, isFilterMenuHidden, sortByDropdownHidden, toggleSortByDropdown, toggleFillterMenu}) => {
    const [toggleDropdown, setToggleDropdown] = useState(false);
    const [isPassedTop, setIsPassedTop] = useState(false)

window.addEventListener('scroll', event => {
    if(window.pageYOffset > 150) {
        setIsPassedTop(true)
    } else {
        setIsPassedTop(false)
    }
})

    return (
         <FilterHeaderContainer>
            <Title isPassedTop = {isPassedTop}>{title}</Title>
            <FilterOptionsContainer>
                <FilterOption onClick = {() => toggleFillterMenu()}>
                    {isFilterMenuHidden ? 'Show' : 'Hide'} Filters
                    <FilterIcon hidden = {isFilterMenuHidden}></FilterIcon>
                </FilterOption>
                <FilterOption 
                    onClick = {() => toggleSortByDropdown()}
                    toggleDropdown = {sortByDropdownHidden}>
                    Sort By 
                    <ArrowIcon toggleDropdown = {sortByDropdownHidden}>
                        <span class = 'arrow-left'></span>
                        <span class = 'arrow-left'></span>
                    </ArrowIcon>  
                </FilterOption>
            </FilterOptionsContainer>
            <SortDropdownContainer toggleDropdown = {sortByDropdownHidden}>
                <span data-type='high' onClick = {handleClick}>Price: High-Low</span>
                <span data-type='low' onClick = {handleClick}>Price: Low-High</span>
            </SortDropdownContainer>
        </FilterHeaderContainer>
    )
}

const mapStateToProps = createStructuredSelector({
    isFilterMenuHidden: selectFilterMenu,
    sortByDropdownHidden : selectSortByDropdown
})

const mapDispatchToProps = dispatch => ({
    toggleFillterMenu: () => dispatch(toggleFillterMenu()),
    toggleSortByDropdown: () => dispatch(toggleSortByDropdown())
})

export default connect(mapStateToProps,mapDispatchToProps)(FilterHeader)
