import styled from 'styled-components'

export const FilterMenuContainer = styled.div`
    height: 2000px;
    min-width: 300px;
    background: white;
    padding-right: 30px;
    padding-bottom: 50px;
    grid-row: 1/-1;
    grid-column: 1/2;
    transition: all .5s ease;
    opacity: ${props => props.isVisible ? 0 : 1};
    transform: ${props => props.isVisible ? 'translateX(-500px)' : 'translateX(0px)'};
`

export const TagsListContainer = styled.ul`
    list-style-type: none;
    padding-bottom: 20px;
    border-bottom: 1px solid rgba(18, 18, 18, .2);
`

export const TagItem = styled.li`
    padding: 8px 0;
    font-weight: 500;
    font-size: 20px;
    cursor: pointer;

    &:hover {
        color: #757575;
    }
`

export const FilterOptionsContainer = styled.div`
    border-bottom: 1px solid rgba(18, 18, 18, .2);
`

export const FilterOptionsHeader = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 13px 0;
`

export const FilterOptions = styled.div`
    padding: 15px 0;
    height: ${props => props.isOpen ? 'auto' : '0px'};
`

export const FormGroup = styled.div`
    padding: 5px 0;
    position: relative;
    display: flex;
    align-items: center;
    transition: all .5s ease;

    input[type="checkbox"] {
        margin-right: 5px;
        width: 22px;
        height: 22px;
        opacity: 0;
    }

    label {
        font-weight: 400;
        font-size: 17px;
    }
`

export const CustomCheckbox = styled.div`
    position: absolute;
    left: 0;
    top: 50%;
    transform: translateY(-50%);
    width: 22px;
    height: 22px;
    border: 1px solid black;
    border-radius: 3px;
    background: black;

    &:after {
        content: "";
        position: absolute;
        left: 50%;
        top: 50%;
        transform: translate(-50%, -50%) rotate(45deg);
        width: 5px;
        height: 10px;
        border: solid white;
        border-width: 0 3px 3px 0;
    }
`
