import React, {useState, useEffect} from 'react'

import CollectionItem from '../../components/collection-item/collection-item.component'
import FilterMenu from '../../components/filter-menu/filter-menu.component'

import CollectionPreview from '../../components/preview-collection/preview-collection.component'
import {CollectionItemContainer, CollectionPageContainer, CollectionContainer} from './collection-container.styles'

import FilterHeader from '../../components/filter-header/filter-header.component'

const CollectionPage = ({ items, title }) => {
    const [isOpen, setIsOpen] = useState(true)    

    const tags = items.map(el => el.tags).flat(1);
    const tagsFlatMap = [...new Set(tags)]

    const handleClick = event => {
        const value = event.target.textContent
    }

    return (
    <CollectionContainer>
        <FilterHeader title = {title} isOpen = {isOpen} setIsOpen = {setIsOpen} />
        <CollectionPageContainer>
            <FilterMenu items = {tagsFlatMap} isVisible = {isOpen} handleClick = {handleClick}/>
            <CollectionItemContainer isOpen = {isOpen}>
                {
                    isDisplayAllItems ?
                    <CollectionsOverview />
                    :
                    <CollectionPreview items = {items}/>
                }
            </CollectionItemContainer>
        </CollectionPageContainer>
    </CollectionContainer>
)}

export default CollectionPage
