import React, {useState} from 'react'
import {connect} from 'react-redux'
import {createStructuredSelector} from 'reselect'

import {selectCollectionTags, selectFilterMenu} from '../../redux/shop/shop.selectors'

import {ArrowIcon} from '../icons/icons.component'

import {FilterMenuContainer, 
        TagsListContainer,
        TagItem,
        FilterOptionsHeader,
        FilterOptionsContainer,
        FilterOptions,
        FormGroup,
        CustomCheckbox
    } from './filter-menu.styles'

const FilterOption = ({label, min, max, id, isPriceLabel}) => {
    let filterLabel = '';

    if(isPriceLabel) {
        filterLabel = max ? `CAD ${min} - CAD ${max} `
        : `CAD ${min}+`
    }
    else {
        filterLabel = {label}
    }

    return (
        <FormGroup key = {id}>
            <CustomCheckbox />
            <input type="checkbox" id={label} name={label} value={label} />
            <label for={label}>
                {filterLabel}
            </label>
        </FormGroup> 

)}

const GENDER_FILTER_DATA = [
    {
        id: 0,
        label: 'men'
    },
    {
        id: 1,
        label: 'women'
    },
    {
        id: 2,
        label: 'kids'
    }
];

const PRICE_FILTER_DATA = [
    {
        id: 0,
        label: 'first',
        min: 0,
        max: 74
    },
    {
        id: 1,
        label: 'second',
        min: 74,
        max: 150
    },
    {
        id: 2,
        label: 'third',
        min: 150,
        max: 220
    },
    {
        id: 3,
        label: 'fourth',
        min: 220
    }
]

const FilterMenu = ({tags, hidden, fetchFilteredCollection}) => {

    const tagsFlatMap = [...new Set(tags[0])]
    const [isOpen, setIsOpen] = useState(false)

    return (
        <FilterMenuContainer isVisible = {hidden}>
           <TagsListContainer>
               {
                tagsFlatMap.map(item => (
                       <TagItem onClick = {fetchFilteredCollection}>{item}</TagItem>
                   ))
               }
           </TagsListContainer>

           <FilterOptionsContainer>
               <FilterOptionsHeader>
                   <h4>Gender</h4>
                   <ArrowIcon />
               </FilterOptionsHeader>

               <FilterOptions>
                    {
                        GENDER_FILTER_DATA.map(({item, ...props })=> (
                            <FilterOption {...props} />
                        ))
                    }
                </FilterOptions>
           </FilterOptionsContainer>

           <FilterOptionsContainer>
               <FilterOptionsHeader onClick = {() => setIsOpen(!isOpen)}>
                   <h4>Shop By Price</h4>
                   <ArrowIcon />
               </FilterOptionsHeader>

               <FilterOptions isOpen = {isOpen}>
                    {
                        PRICE_FILTER_DATA.map(({item, ...props })=> (
                            <FilterOption isPriceLabel {...props} />
                        ))
                    }

                </FilterOptions>
           </FilterOptionsContainer>
        </FilterMenuContainer>
    )
}

const mapStateToProps = createStructuredSelector({
    tags: selectCollectionTags,
    hidden: selectFilterMenu,
})

export default connect(mapStateToProps)(FilterMenu)
