import React from 'react'

const ImagePreview = (item, handleClick) => {
const {imageUrl, images, name} = item;
    return (
    <>
        <img src = {imageUrl} alt = {name} onClick = {handleClick} className = 'image'/>
            {images.map(image => (
            <img src={image} alt={name} onClick = {handleClick}/>
        ))}
    </>
    )
}

export default ImagePreview;
