import React, {useState} from 'react'
import {animated, useSpring, useTransition, config} from 'react-spring'

import {connect} from 'react-redux'
import {selectCollection} from '../../redux/shop/shop.selectors'

import CollectionItem from '../../components/collection-item/collection-item.component'
import FilterMenu from '../../components/filter-menu/filter-menu.component'

import './collection.styles.scss'

const CollectionPage = ({ collection }) => {
    const [isOpen, setIsOpen] = useState(true)
    const {title, items} = collection

    const transitions = useTransition(isOpen, null, {
        config: config.default,
        from: {transform: 'translateX(-100px)', opacity: 0},
        enter: {transform: 'translateX(0px)', opacity: 1},
        leave: {transform: 'translateX(-100px)', opacity: 0}
    })

    return (
    <div className="collection-page">
        <div className="collection-page-header">
        <h2 className="title">{title}</h2>
        </div>
        <button onClick = {() => setIsOpen(!isOpen)}>TOGGLE</button>
        {transitions.map(({ item, props}) => item && (
        <animated.div className="collection-page-container">
            <FilterMenu isVisible = {isOpen}/>
            <div className="items">
                {items.map(item => 
                    <CollectionItem key = {item.id} item = {item}/>
                )}
            </div>
        </animated.div>
        ))}
    </div>
)}

const mapStateToProps = (state, ownProps) => ({
    collection: selectCollection(ownProps.match.params.collectionId)(state)
})

export default connect(mapStateToProps)(CollectionPage)
