import React from 'react'
import {connect} from 'react-redux'
import {selectItemDetails, selectCollectionForPreview} from '../../redux/shop/shop.selectors'
import CollectionDetails from '../../components/collection-details/collection-details.component'
import ScrollingContainer from '../../components/scrolling-wrapper/scrolling-wrapper.component'

const CollectionDetailsPage = ({collections, similarCollections}) => {

    const similar = similarCollections.map(collection => collection.items.filter((item, idx) => idx < 12 && item.tags.includes('lifestyle')))

    return (
        <div className = 'collection-details-page'>
           {collections.map(collection => (
               <CollectionDetails items = {collection}/>
            ))
            }
            <ScrollingContainer collections = {similar} />
        </div>
    )
}

const mapStateToProps = (state, ownProps) => ({
    collections: selectItemDetails(ownProps.match.params.itemId)(state),
    similarCollections: selectCollectionForPreview(state)
})

export default connect(mapStateToProps)(CollectionDetailsPage)
