import React, { useState } from 'react'
import {connect} from 'react-redux'
import {animated, useSpring, useTransition, config} from 'react-spring'

import FormInput from '../form-input/form-input.component'
import CustomButton from '../custom-button/cutom-button.component'

import { auth, signInWithGoogle } from '../../firebase/firebase.utils'

import {emailSignInStart,
        googleSignInStart
        } from '../../redux/user/user.actions'

import './sign-in.styles.scss'
import Logo from '../logo-container/logo-container.component'

const SignIn = ({emailSignInStart, googleSignInStart, hidden}) => {
    const [userCredentials, setCredentials] = useState({
            email: '', 
            password: ''
        })

    const {email, password} = userCredentials;

    const handleSubmit = async event => {
        event.preventDefault();
        emailSignInStart(email, password)
    }

    const handleChange = event => {
        const { name, value } = event.target;

        setCredentials({
            ...userCredentials,
            [name]: value
        })
    }


    const transitions = useTransition(!hidden, null, {
        config: config.default,
        from: {opacity: 0},
        enter: {opacity: 1},
        leave: {opacity: 0}
    })

    return transitions.map(({ item, props}) => item && (
        <animated.div style = {props} className="sign-in-container">
            <div className='sign-in'>
                <div className="sign-in-header">
                    <Logo small/>
                    <h2>Your account for everything Nike</h2>
                </div>

                <form onSubmit = {handleSubmit}>
                    <FormInput 
                        type = 'email'
                        name = 'email' 
                        value= {email}
                        handleChange = {handleChange}
                        placeholder = 'Email address'
                        required/>

                    <FormInput 
                        type = 'password'
                        name = 'password' 
                        value= {password}
                        handleChange = {handleChange}
                        placeholder = 'Password'
                        required/>

                    <FormInput 
                        type = 'checkbox'
                        name = 'checkbox' 
                        checked
                        handleChange = {handleChange}
                        label = 'I agree to the *terms and conditions'
                        required/>
                    <CustomButton type= 'submit'>
                        Sign In
                    </CustomButton>
                    <CustomButton type= 'button' onClick = { googleSignInStart } isGoogleSignIn>
                        Sign In With Google
                    </CustomButton>
                </form>
            </div>
        </animated.div>
        )
    )}


const mapDispatchToProps = dispatch => ({
    googleSignInStart: () => dispatch(googleSignInStart()),
    emailSignInStart: (email, password) =>
      dispatch(emailSignInStart({ email, password }))
  });

export default connect(
    null, 
    mapDispatchToProps
    )(SignIn)
