import React from 'react'
import {connect} from 'react-redux'
import {createStructuredSelector} from 'reselect'

import {selectCollectionTags} from '../../redux/shop/shop.selectors'

import {FilterMenuContainer, 
        TagsListContainer,
        TagItem} from './filter-menu.styles'

const FilterMenu = ({isVisible, tags, handleClick, items}) => {
    const props = useSpring({
        opacity: isVisible ? 0 : 1
    })
    const tagsFlatMap = [...new Set(tags[0])]

    return (
        <FilterMenuContainer>
           <TagsListContainer>
               {
                tagsFlatMap.map(item => (
                       <TagItem onClick = {handleClick}>{item}</TagItem>
                   ))
               }
           </TagsListContainer>
        </FilterMenuContainer>
    )
}

const mapStateToProps = createStructuredSelector({
    tags: selectCollectionTags
})

export default connect(mapStateToProps)(FilterMenu)
