import React, {useEffect} from 'react'
import {Route} from 'react-router-dom'
import {createStructuredSelector} from 'reselect'
import {connect} from 'react-redux'

import {fetchCollectionsStartAsync} from '../../redux/shop/shop.actions'
import {selectIsCollectionFetching, 
        selectIsCollectionLoaded} from '../../redux/shop/shop.selectors'

import CollectionsOverviewContainer from '../../components/collections-overview/collections-overview.container'
import CollectionPageContainer from '../collection/collection.container'
import CollectionDetailsContainer from '../collection-details/collection-details.container'

import WithSpinner from '../../components/with-spinner/with-spinner.component'

import {updateCollections} from '../../redux/shop/shop.actions'

import './shop.styles.scss'


const ShopPage = ({match, isLoaded, fetchCollectionsStartAsync}) => {

    useEffect(() => {
        fetchCollectionsStartAsync()
    }, [])

    return(
        <div className = 'shop-page'>
            <Route 
                exact path ={`${match.path}`} 
                component = {CollectionsOverviewContainer}
            />
            <Route 
                exact path = {`${match.path}/:collectionId`} 
                component = {CollectionPageContainer}
            />
            <Route 
                exact path = {`${match.path}/details/:itemId`} 
                component = {CollectionDetailsContainer}
            />
        </div>
    )
}

const mapStateToProps = createStructuredSelector({
    isLoaded: selectIsCollectionLoaded,
})

const mapDispatchToProps = dispatch => ({
    fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
})

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage)
