import styled from 'styled-components'

export const ArrowIconContainer = styled.div`
    width: 1rem;
    height: 1.25rem;
    position: relative;
    margin-left: 5px;
    z-index: 100000;

    span {
        width: 10px;
        height: 2px;
        background: #333;
        display: inline-block;
        position: absolute;
        top: .6rem;
        transition: all .3s ease;

        &:first-of-type {
            left: 0;
            transform: ${props => props.isDropdownHidden ? 'rotate(45deg)' : 'rotate(-45deg)'};
        }

        &:last-of-type {
            right: 0;
            transform: ${props => props.isDropdownHidden ? 'rotate(-45deg)' : 'rotate(45deg)'};
        }
    }
`
