import styled from 'styled-components'

export const SignInContainer = styled.div`
    width:450px;
    display: flex;
    flex-direction: column;
    background: white;
    padding: 50px;
    transition: all .5s ease;
    transform: ${props => props.hidden ? 'translateX(500px)' : 'translateX(0px)'}
`
