export const addItemToCart = (cartItems, cartItemToAdd) => {
    const exisitingCartItem = cartItems.find(
        cartItem => cartItem.id === cartItemToAdd.id)

    const exisitingCartItemSize = cartItems.find(
        cartItem => cartItem.size === cartItemToAdd.size
    )

        if(exisitingCartItem && exisitingCartItemSize) {
            return cartItems.map(cartItem => 
                cartItem.id === cartItemToAdd.id && cartItem.size === cartItemToAdd.size 
                ? { ...cartItem, quantity: cartItem.quantity + 1, size: cartItemToAdd.size }
                : cartItem  
            )
        }

        return [...cartItems, { ...cartItemToAdd, quantity: 1, size: cartItemToAdd.size}]
}

export const removeItemFromCart = (cartItems, cartItemToRemove) => {
    const exisitingCartItem = cartItems.find(
        cartItem => cartItem.id === cartItemToRemove.id && cartItem.size === cartItemToRemove.size
    )

    const exisitingCartItemSize = cartItems.find(
        cartItem => cartItem.size === cartItemToRemove.size
    )

    if(exisitingCartItem.quantity === 1) {
        return cartItems.filter(cartItem => cartItem.id !== cartItemToRemove.id && cartItem.size !== cartItemToRemove.size)
    }

    return cartItems.map(cartItem => cartItem.id === cartItemToRemove.id && cartItem.size === cartItemToRemove.size ? 
            { ...cartItem, quantity: cartItem.quantity - 1}
            : cartItem
        ) 
}
