import styled from 'styled-components'

export const FilterMenuContainer = styled.div`
    min-width: 300px;
    background: white;
    padding-right: 30px;
    padding-bottom: 50px;
    grid-row: 1/-1;
    grid-column: 1/2;
    transition: all .5s ease;
    opacity: ${props => props.isVisible ? 0 : 1};
    transform: ${props => props.isVisible ? 'translateX(-500px)' : 'translateX(0px)'};

    @media(max-width: 1100px) {
        position: absolute;
        z-index: 100000;
    }
`

export const TagsListContainer = styled.ul`
    list-style-type: none;
    padding-bottom: 20px;
    border-bottom: 1px solid rgba(18, 18, 18, .2);
`

export const TagItem = styled.li`
    padding: 6px 0;
    font-weight: 500;
    font-size: 16px;
    cursor: pointer;
    text-transform: capitalize;

    &:hover {
        color: #757575;
    }
`

export const FilterOptionsContainer = styled.div`
    border-bottom: 1px solid rgba(18, 18, 18, .2);
`

export const FilterOptionsHeader = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 18px 0;
    width: 100%;
`

export const CollapsibleContainer = styled.div`
    max-height: ${props => props.isOpen ? '100vh' : '0px'};
    overflow: hidden;
    transition: max-height .25s ease-in-out;
`

export const FilterOptions = styled.div`
    padding-bottom: ${props => props.isOpen ? '12px' : '0px'};
    transition: all .3s linear;
`
