import styled from 'styled-components'

export const CollectionItemContainer = styled.div`
    position: relative;
    padding: 5px;
    background: #fff;
    width: 100%;
    margin-bottom: 20px;
`

export const ImageContainer = styled.img`
    width: 100%;
    object-fit: cover;
    margin-bottom: 15px;
    height: 400px;
    transform: ${props => props.isLoading ? 'translateY(50px)' : 'translateY(0px)'};
    transition: opacity 5s ease;
`

export const ImageGridContainer = styled.div`
    width: 100%;
    margin-top: 8px;  
    display: flex;
    align-items: center;
`

export const ImagePreviewContainer = styled.div`
    height: 60px;
    display: flex;
`

export const Image = styled.img`
    width: 39px;
    margin: 0 2px;
`
