import styled from 'styled-components'
import {Link} from 'react-router-dom'

export const FooterContainer = styled.div`
    display: grid;
    grid-template-columns: 70% 30%;
    background: #111;
    padding: 50px;
`

export const NavigationListsContainer = styled.div`
    display: grid;
    grid-template-columns: repeat(3, 1fr);
`

export const NavigationListContainer = styled.ul`
    list-style-type: none;
`

export const NavigationItem = styled.li`
    color: ${props => props.isCapitalized ? '#fff' : '#8d8d8d'};
    padding: 5px 0;
    text-transform: ${props => props.isCapitalized ? 'uppercase': 'normal'};
`

export const Title = styled.h2`
    color: #fff;
    text-transform: uppercase;
    font-size: 15px;
    margin-bottom: 15px;
`

export const OptionLink = styled(Link)`
    background: #8d8d8d;
    width: 30px;
    height: 30px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50%;
    margin: 10px;
    opacity: .9;

    i {
        color: #111;
    }
`

export const ContactLinksContainer = styled.div`
    display: flex;
    justify-content: flex-end;
    align-items: flex-start;
`
