import React, {Fragment, useState} from 'react'

import Checkbox from '../checkbox/checkbox.component'

import './size-chart.styles.scss'

const SizeChart = ({item, handleSubmit, isValid, children, ...props}) => {
    const [isChecked, setIsChecked] = useState(false)
    const [size, setSize] = useState()

    const handleChange = event => {
        setIsChecked(true)
        setSize(event.target.value)
    }

    let links = []
    for(let i = 5; i < 12; i++) {
        links.push(
        <Fragment>
           <Checkbox type = {i} {...props} handleChange = {handleChange}/>
           <Checkbox type = {`${i}.5`} {...props} handleChange = {handleChange}/>
        </Fragment>
        )
    }

    return (
        <form className = {`size-chart ${isValid && 'error-message'}`} onSubmit = {() => item.push({size: size})}>
            <h3>Select Size</h3>
            {links}
            {children}
        </form>
    )
}

export default SizeChart
